import React, { useState, useEffect } from 'react';
import WeatherCard from '../components/WeatherCard';
import AIRecommendations from '../components/AIRecommendations';
import FavoritesSection from '../components/FavoritesSection';

const Home = ({ currentLocation, setCurrentLocation, favorites, setFavorites }) => {
  const [weatherData, setWeatherData] = useState({
    location: 'Chennai',
    condition: 'Clear',
    temperature: 22,
    humidity: 50,
    feelsLike: 24,
    wind: 5,
    icon: '‚òÄÔ∏è'
  });

  const [isGeneratingRecommendations, setIsGeneratingRecommendations] = useState(false);

  const generateAIRecommendations = () => {
    setIsGeneratingRecommendations(true);
    // Simulate AI generation
    setTimeout(() => {
      setIsGeneratingRecommendations(false);
    }, 3000);
  };

  useEffect(() => {
    // Simulate fetching weather data
    const mockWeatherData = {
      'Chennai': { location: 'Chennai', condition: 'Clear', temperature: 22, humidity: 50, feelsLike: 24, wind: 5, icon: '‚òÄÔ∏è' },
      'Mumbai': { location: 'Mumbai', condition: 'Partly Cloudy', temperature: 28, humidity: 70, feelsLike: 32, wind: 8, icon: '‚õÖ' },
      'Delhi': { location: 'Delhi', condition: 'Hazy', temperature: 35, humidity: 40, feelsLike: 40, wind: 3, icon: 'üå´Ô∏è' }
    };
    
    setWeatherData(mockWeatherData[currentLocation] || mockWeatherData['Chennai']);
  }, [currentLocation]);

  return (
    <div>
      <FavoritesSection 
        favorites={favorites}
        currentLocation={currentLocation}
        setCurrentLocation={setCurrentLocation}
      />
      
      <WeatherCard 
        weatherData={weatherData}
        onSave={() => {
          if (!favorites.includes(weatherData.location)) {
            setFavorites([...favorites, weatherData.location]);
          }
        }}
        onRefresh={() => {
          // Simulate refresh
          console.log('Refreshing weather data...');
        }}
      />
      
      <AIRecommendations 
        weatherData={weatherData}
        isGenerating={isGeneratingRecommendations}
        onGenerate={generateAIRecommendations}
      />
    </div>
  );
};

export default Home;
